package base

import ccxt "github.com/ccxt/ccxt/go/v4"

// PLEASE DO NOT EDIT THIS FILE, IT IS GENERATED AND WILL BE OVERWRITTEN:
// https://github.com/ccxt/ccxt/blob/master/CONTRIBUTING.md#how-to-contribute-code

func TestUuid() {
	exchange := ccxt.NewExchange().(*ccxt.Exchange)
	exchange.DerivedExchange = exchange
	exchange.InitParent(map[string]interface{}{
		"id": "sampleexchange",
	}, map[string]interface{}{}, exchange)
	// uuid() - standard UUID v4: xxxxxxxx-xxxx-4xxx-[89ab]xxx-xxxxxxxxxxxx
	var id1 interface{} = exchange.Uuid() // need type for .length understanding
	var id2 interface{} = exchange.Uuid()
	assert(!ccxt.IsEqual(id1, nil), "uuid 1 must return a value")
	assert(!ccxt.IsEqual(id2, nil), "uuid 2 must return a value")
	assert(!ccxt.IsEqual(id1, id2), "uuid() must return unique values on each call")
	assert(ccxt.IsEqual(ccxt.ToString(id1), id1), "uuid() must return a string")
	assert(ccxt.IsEqual(ccxt.ToString(id2), id2), "uuid() must return a string")
	assert(ccxt.IsEqual(ccxt.GetLength(id1), 36), "uuid() must return a 36-character string")
	assert(ccxt.IsEqual(ccxt.GetLength(id2), 36), "uuid() must return a 36-character string")
	assert(ccxt.IsEqual(ccxt.GetIndexOf(id1, "-"), 8), "uuid() must have dash")
	// uuid16() - 16-char hex string
	var id16a interface{} = exchange.Uuid16()
	var id16b interface{} = exchange.Uuid16()
	assert(!ccxt.IsEqual(id16a, nil), "uuid16 1 must return a value")
	assert(!ccxt.IsEqual(id16b, nil), "uuid16 2 must return a value")
	assert(!ccxt.IsEqual(id16a, id16b), "uuid16() must return unique values on each call")
	assert(ccxt.IsEqual(ccxt.ToString(id16a), id16a), "uuid16() must return a string")
	assert(ccxt.IsEqual(ccxt.ToString(id16b), id16b), "uuid16() must return a string")
	assert(ccxt.IsEqual(ccxt.GetLength(id16a), 16), "uuid16() must return a 16-character string")
	assert(ccxt.IsEqual(ccxt.GetLength(id16b), 16), "uuid16() must return a 16-character string")
	// uuid22() - 22-char hex string
	var id22a interface{} = exchange.Uuid22()
	var id22b interface{} = exchange.Uuid22()
	assert(!ccxt.IsEqual(id22a, nil), "uuid22 1 must return a value")
	assert(!ccxt.IsEqual(id22b, nil), "uuid22 2 must return a value")
	assert(!ccxt.IsEqual(id22a, id22b), "uuid22() must return unique values on each call")
	assert(ccxt.IsEqual(ccxt.ToString(id22a), id22a), "uuid22() must return a string")
	assert(ccxt.IsEqual(ccxt.ToString(id22b), id22b), "uuid22() must return a string")
	assert(ccxt.IsEqual(ccxt.GetLength(id22a), 22), "uuid22() must return a 22-character string")
	assert(ccxt.IsEqual(ccxt.GetLength(id22b), 22), "uuid22() must return a 22-character string")
}
