function test_unique() {
    $exchange = new \ccxt\async\Exchange(array(
        'id' => 'sampleexchange',
    ));
    // in different langs, the order (sort) is not guaranteed, so we sort the results before comparing them
    assert_deep_equal($exchange, null, 'testUnique', $exchange->unique([]), []);
    assert_deep_equal($exchange, null, 'testUnique', $exchange->sort($exchange->unique([1, 2, 3])), [1, 2, 3]);
    assert_deep_equal($exchange, null, 'testUnique', $exchange->sort($exchange->unique([1, 2, 3, 4, 1])), [1, 2, 3, 4]);
    assert_deep_equal($exchange, null, 'testUnique', $exchange->sort($exchange->unique(['a', 'a', 'b', 'c', 'a', 'c'])), ['a', 'b', 'c']);
}
