function test_uuid() {
    $exchange = new \ccxt\async\Exchange(array(
        'id' => 'sampleexchange',
    ));
    // uuid() - standard UUID v4: xxxxxxxx-xxxx-4xxx-[89ab]xxx-xxxxxxxxxxxx
    $id1 = $exchange->uuid(); // need type for .length understanding
    $id2 = $exchange->uuid();
    assert($id1 !== null, 'uuid 1 must return a value');
    assert($id2 !== null, 'uuid 2 must return a value');
    assert($id1 !== $id2, 'uuid() must return unique values on each call');
    assert(((string) $id1) === $id1, 'uuid() must return a string');
    assert(((string) $id2) === $id2, 'uuid() must return a string');
    assert(strlen($id1) === 36, 'uuid() must return a 36-character string');
    assert(strlen($id2) === 36, 'uuid() must return a 36-character string');
    assert(mb_strpos($id1, '-') === 8, 'uuid() must have dash');
    // uuid16() - 16-char hex string
    $id16a = $exchange->uuid16();
    $id16b = $exchange->uuid16();
    assert($id16a !== null, 'uuid16 1 must return a value');
    assert($id16b !== null, 'uuid16 2 must return a value');
    assert($id16a !== $id16b, 'uuid16() must return unique values on each call');
    assert(((string) $id16a) === $id16a, 'uuid16() must return a string');
    assert(((string) $id16b) === $id16b, 'uuid16() must return a string');
    assert(strlen($id16a) === 16, 'uuid16() must return a 16-character string');
    assert(strlen($id16b) === 16, 'uuid16() must return a 16-character string');
    // uuid22() - 22-char hex string
    $id22a = $exchange->uuid22();
    $id22b = $exchange->uuid22();
    assert($id22a !== null, 'uuid22 1 must return a value');
    assert($id22b !== null, 'uuid22 2 must return a value');
    assert($id22a !== $id22b, 'uuid22() must return unique values on each call');
    assert(((string) $id22a) === $id22a, 'uuid22() must return a string');
    assert(((string) $id22b) === $id22b, 'uuid22() must return a string');
    assert(strlen($id22a) === 22, 'uuid22() must return a 22-character string');
    assert(strlen($id22b) === 22, 'uuid22() must return a 22-character string');
}
