import ccxt.async_support as ccxt  # noqa: F402

def test_uuid():
    exchange = ccxt.Exchange({
        'id': 'sampleexchange',
    })
    # uuid() - standard UUID v4: xxxxxxxx-xxxx-4xxx-[89ab]xxx-xxxxxxxxxxxx
    id1 = exchange.uuid()  # need type for .length understanding
    id2 = exchange.uuid()
    assert id1 is not None, 'uuid 1 must return a value'
    assert id2 is not None, 'uuid 2 must return a value'
    assert id1 != id2, 'uuid() must return unique values on each call'
    assert str(id1) == id1, 'uuid() must return a string'
    assert str(id2) == id2, 'uuid() must return a string'
    assert len(id1) == 36, 'uuid() must return a 36-character string'
    assert len(id2) == 36, 'uuid() must return a 36-character string'
    assert id1.find('-') == 8, 'uuid() must have dash'
    # uuid16() - 16-char hex string
    id16a = exchange.uuid16()
    id16b = exchange.uuid16()
    assert id16a is not None, 'uuid16 1 must return a value'
    assert id16b is not None, 'uuid16 2 must return a value'
    assert id16a != id16b, 'uuid16() must return unique values on each call'
    assert str(id16a) == id16a, 'uuid16() must return a string'
    assert str(id16b) == id16b, 'uuid16() must return a string'
    assert len(id16a) == 16, 'uuid16() must return a 16-character string'
    assert len(id16b) == 16, 'uuid16() must return a 16-character string'
    # uuid22() - 22-char hex string
    id22a = exchange.uuid22()
    id22b = exchange.uuid22()
    assert id22a is not None, 'uuid22 1 must return a value'
    assert id22b is not None, 'uuid22 2 must return a value'
    assert id22a != id22b, 'uuid22() must return unique values on each call'
    assert str(id22a) == id22a, 'uuid22() must return a string'
    assert str(id22b) == id22b, 'uuid22() must return a string'
    assert len(id22a) == 22, 'uuid22() must return a 22-character string'
    assert len(id22b) == 22, 'uuid22() must return a 22-character string'
